import './App.css';
import Button from './components/Button';
import React, { useEffect, useState } from "react";
import random from 'random'
import QuoteDiv from "./components/Quote";
import LikeButton from './components/LikeButton';
import { AxiosProvider, Request, Get, Delete, Head, Post, Put, Patch, withAxios } from 'react-axios'
import axios from 'axios';

function App() {

  const anchorHorizontal = React.useRef(null);
  const anchorVertical = React.useRef(null);
  const popupHorizontal = React.useRef(null);
  const popupVertical = React.useRef(null);
  const [anchorAlign, setAnchorAlign] = React.useState({
    horizontal: 'left',
    vertical: 'bottom'
  });
  const [popupAlign, setPopupAlign] = React.useState({
    horizontal: 'right',
    vertical: 'top'
  });
  const [show, setShow] = React.useState(false);
  const onClick = () => {
    setShow(!show);
  };
  //const mongoose = require('mongoose');

  //const url = 'mongodb+srv://bito94:bH7WWgibBDj29DHI@cluster0.c79rp.mongodb.net/myFirstDatabase?retryWrites=true&w=majority';
  //const client = new MongoClient(url);
  //mongoose.connect(url)
  /*
  async function run() {
    try {
        await client.connect();
        console.log("Connected correctly to server");
    } catch (err) {
        console.log(err.stack);
    }
    finally {
        await client.close();
    }
  }
  run().catch(console.dir);
  */



  //state variables
  const [quotesList, setQuotesList] = useState([]);
  const [quote, setQuote] = useState({});
  const [like, setLike] = useState([]);



  const addLike = () => {
    let newCount = 1;
    console.log(newCount);
    setLike(newCount);
    sendGetRequest();
  }
  //returns liked quotes
  const sendGetRequest = async () => {
    try {
        const resp = await axios.get('http://localhost:5000/personnel');
        console.log(resp.data);
    } catch (err) {
        // Handle Error Here
        console.error(err);
    }
};

  let quoteList;

  //this is use to run on start
  useEffect(async () => {
    if (quotesList.length === 0) {
      const quotesResult = await fetch("https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json");
      //console.log(quotesResult.json());
      quoteList = await (quotesResult.json());

      setQuotesList(quoteList.quotes)
    }
    else{
      setRandomQuote(quotesList)
    }
    //if quoteslist isnt added here, it keeps rerendering forever
  },[quotesList])


  const setRandomQuote = list => {
    const randNum = randIndex(list.length);
    while(list[randNum].quote === quote.quote){
      randNum = randIndex(list.length);
    }
    console.log(list[randNum].quote)
    setQuote(list[randNum])
  }

  const randIndex =  (quotesLen) => {
    //console.log(quoteList.quotes.length)
    console.log(quotesLen)
    if(!quotesLen) {
      return;
    }
    return Math.floor(Math.random()*quotesLen);
  }

  function nextQuoteClickHandler(){
    setRandomQuote(quotesList)

  }
  function clickLike(){
    addLike()
  }
  //////////////////////////////////////////
  const onConfigChange = () => {
    let aHorizontal = anchorHorizontal.current;
    let aVertical = anchorVertical.current;
    let pHorizontal = popupHorizontal.current;
    let pVertical = popupVertical.current;

    if (aHorizontal !== null && aVertical !== null) {
      setAnchorAlign({
        horizontal: aHorizontal.value,
        vertical: aVertical.value
      });
    }

    if (pHorizontal !== null && pVertical !== null) {
      setPopupAlign({
        horizontal: pHorizontal.value,
        vertical: pVertical.value
      });
    }
  };


  return (
    <div className="App" id="quote-box" >
      <QuoteDiv 
        value={quote}
        onQuoteChange={() => setRandomQuote(quotesList)}
      />
      <Button buttonDisplayName="Next Quote" clickHandler={nextQuoteClickHandler}/>
      <LikeButton clickHandler={clickLike}/>
      {/*<div>
          <button
            className="button"
            onClick={addLike}
          >addlike
          </button>
      </div>*/}

<div className="example-config row">
        <div className="col-sm-4 col-xs-12">
          <h4>Anchor align point</h4>
          <p>
            Horizontal <br />
            <select onChange={onConfigChange} ref={anchorHorizontal} value={anchorAlign.horizontal} className="k-textbox">
              <option value="left">Left</option>
              <option value="center">Center</option>
              <option value="right">Right</option>
            </select>
          </p>
          <p>
            Vertical <br />
            <select onChange={onConfigChange} ref={anchorVertical} value={anchorAlign.vertical} className="k-textbox">
              <option value="top">Top</option>
              <option value="middle">Middle</option>
              <option value="bottom">Bottom</option>
            </select>
          </p>
        </div>
        <div className="col-sm-4 col-xs-12">
          <h4>Popup align point</h4>
          <p>
            Horizontal <br />
            <select onChange={onConfigChange} ref={popupHorizontal} value={popupAlign.horizontal} className="k-textbox">
              <option value="left">Left</option>
              <option value="center">Center</option>
              <option value="right">Right</option>
            </select>
          </p>
          <p>
            Vertical <br />
            <select onChange={onConfigChange} ref={popupVertical} value={popupAlign.vertical} className="k-textbox">
              <option value="top">Top</option>
              <option value="middle">Middle</option>
              <option value="bottom">Bottom</option>
            </select>
          </p>
        </div>
        <div className="col-sm-4 col-xs-12">
          <h4>Popup action</h4>
          <br />
          <button onClick={onClick} className="k-button k-primary">{show ? 'Close' : 'Open'}</button>
        </div>
      </div>



    </div>
  );
}

export default App;
