import './App.css';
import Button from './components/Button';
import React, { useEffect, useState } from "react";
import random from 'random'


function App() {
  //state variables
  const [text, setText] = useState([]);
  const [author, setAuthor] = useState([]);

  let quoteList;
  const getQuotes = async () => {
    const quotesResult = await fetch("https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json");
    //console.log(quotesResult.json());
    quoteList = await (quotesResult.json());
    //console.log(quoteList.quotes[0].quote);
    //console.log(quoteList.quotes[0].author);
    //let randNum = randIndex();
    console.log(randIndex())
    setText(quoteList.quotes[0].quote);
    setAuthor(quoteList.quotes[0].author);

  }

  const randIndex =  () => {
    //console.log(quoteList.quotes.length)
    if(!quoteList.quotes.length) {
      return;
    }
    return Math.floor(Math.random()*quoteList.quotes.length);
  }

  function nextQuoteClickHandler(){
    console.log("Hi");
  }

  function getRandQuote(){
    return getQuotes();

  }

  console.log(author);

  return (
    <div className="App" id="quote-box">
      { getRandQuote() ?  getRandQuote() : '' }
      <Button buttonDisplayName="Next Quote" clickHandler={nextQuoteClickHandler}/>
    </div>
  );
}

export default App;
